import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

// Create an Axios instance that ignores self-signed certs (for development purposes)
const axiosInstance = axios.create({
  baseURL: 'https://localhost:3001',
  withCredentials: true,
  httpsAgent: new https.Agent({
      rejectUnauthorized: false, // Disable SSL validation
  }),
});

const LoginRegister = () => {
    const [username, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [userFirstName, setUserFirstName] = useState('');
    const [userLastName, setUserLastName] = useState('');
    const [idNumber, setIdNumber] = useState('');
    const [accountNumber, setAccountNumber] = useState('');

    let navigate = useNavigate();

    /*----------------------------- Register Function ----------------------------------*/
    const handleRegister = async (event) => {
      event.preventDefault(); // Prevent the default form submission behavior
      const data = {
          username,
          userFirstName,
          userLastName,
          password,
          idNumber,
          accountNumber,
      };
  
      try {
          const response = await axiosInstance.post('https://localhost:3001/api/register', data, {
              withCredentials: true, // Ensure cookies are sent with the request
          });
  
          if (response.data.message) {
              alert(response.data.message); // Show success message
              navigate('/login'); // Redirect to login page after successful registration
          }
      } catch (error) {
          console.log('Error details:', error);
          if (error.response && error.response.data && error.response.data.error) {
              alert(error.response.data.error); // Show specific error from backend
          } else {
              alert('An error occurred during registration.');
          }
      }
  };


    /*----------------------------- Login Function ----------------------------------*/
    const handleLogin = async (event) => {
        event.preventDefault(); // Prevent the form from reloading the page

        const data = {
            username,
            password,
            accountNumber
        };

        try {
            const response = await axiosInstance.post('https://localhost:3001/api/login', data, {
                withCredentials: true, // Ensure cookies are sent with the request
            });

            if (response.status === 200) {
                navigate('/home'); // Redirect to home after successful login
            } else {
                alert('Invalid login credentials');
            }
        } catch (error) {
            alert('Invalid login credentials');
        }
    };

    /*----------------------------- Switch Control (Switch between Register and Login) ----------------------------------*/
    function SwitchContent(e) {
        const content = document.getElementById('content');
        const registerBtn = document.getElementById('register');
        const loginBtn = document.getElementById('login');

        // Simplified the toggling logic for switching between login/register
        if (e.target.id === 'login') {
            content.classList.remove("active");
        } else if (e.target.id === 'register') {
            content.classList.add("active");
        }
    }

    /*----------------------------- Frontend Code ----------------------------------*/
    return (
        <div className="content justify-content-center align-items-center d-flex shadow-lg" id="content">
          {/*----------------------------- Register Form ----------------------------------*/}
          <div className="col-md-6 d-flex justify-content-center">
            <form onSubmit={handleRegister}>
              <div className="header-text mb-4">
                  <h1>Register for Online Banking</h1>
              </div>
              
                <div className="input-group mb-3">
                  <input
                    className="form-control form-control-lg bg-light fs-6"
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUserName(e.target.value)}
                    required
                  />
                </div>
      
                <div className="input-group mb-3">
                  <input
                    className="form-control form-control-lg bg-light fs-6"
                    type="text"
                    name="userFirstName"
                    placeholder="First name"
                    value={userFirstName}
                    onChange={(e) => setUserFirstName(e.target.value)}
                    required
                  />
                </div>
      
                <div className="input-group mb-3">
                 <input
                    className="form-control form-control-lg bg-light fs-6"
                    type="text"
                    name="userLastName"
                    placeholder="Last name"
                    value={userLastName}
                    onChange={(e) => setUserLastName(e.target.value)}
                    required
                  />
                </div>
      
                <div className="input-group mb-3">
                  <input
                    className="form-control form-control-lg bg-light fs-6"
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
      
                <div className="input-group mb-3">
                  <input
                    className="form-control form-control-lg bg-light fs-6"
                    type="text"
                    name="idNumber"
                    placeholder="ID number"
                    value={idNumber}
                    onChange={(e) => setIdNumber(e.target.value)}
                    required
                  />
                </div>
      
                <div className="input-group mb-3">
                  <input
                    className="form-control form-control-lg bg-light fs-6"
                    type="text"
                    name="accountNumber"
                    placeholder="Account number"
                    value={accountNumber}
                    onChange={(e) => setAccountNumber(e.target.value)}
                    required
                  />
                </div>
      
                <div className="input-group mb-5 d-flex justify-content-center">
                <div className="form-check">
                  <input
                    type="checkbox"
                    name="terms"
                    value="checked"
                    className="form-check-input"
                    required
                  />
                    <label htmlFor='formcheck' className="form-check-label text-secondary">
                    <small> I agree to the Terms and Service that my data will be taken and sold.  </small>
                    </label>    
                  </div>
                </div>
      
                <div className="input-group mb-3 justify-content-center"> 
                  <button
                    className="btn border-whote text-white w-50 fs-6"
                    type="submit" // Changed this from onClick to onSubmit to trigger the form submission
                  >
                    Register
                  </button>
                </div>
            </form>
          </div>

          {/*----------------------------- Login Form ----------------------------------*/}
          <div className="col-md-6 right-box">
            <form onSubmit={handleLogin}>
              <div className="header-text mb-4">
                <h1>Login</h1>
              </div>

              <div className="input-group mb-3">
                <input 
                  type="text" 
                  name="username" 
                  placeholder="Username"
                  value={username} 
                  onChange={(e) => setUserName(e.target.value)} />
              </div>

              <div className="input-group mb-3">
                <input 
                  type="password" 
                  name="password" 
                  placeholder="Password"
                  value={password} 
                  onChange={(e) => setPassword(e.target.value)} />
              </div>

              <div className="input-group mb-3">
                <input 
                  type="text" 
                  name="accountNumber" 
                  placeholder="Account number"
                  value={accountNumber} 
                  onChange={(e) => setAccountNumber(e.target.value)} 
                  required />
              </div>

              <div className="input-group mb-3 justify-content-center"> 
                <button
                  className="btn border-whote text-white w-50 fs-6"
                  type="submit"
                >
                  Login
                </button>
              </div>
            </form>
          </div>

          {/*----------------------------- Switch Panel ----------------------------------*/}     
          <div className="switch-content">
            <div className="switch">
              <div className="switch-panel switch-left">
                <h1>Hello, Again</h1>
                <p>We are happy to see you back</p>
                <button className='hidden btn border-white text-white w-50 fs-6' id="login" onClick={SwitchContent}>Login</button>
              </div>
              <div className="switch-panel switch-right">
                <h1>Welcome</h1>
                <p> Join us today to experience secure, seamless banking services tailored to your global needs.</p>
                <button className='hidden btn border-white text-white w-50 fs-6' id='register' onClick={SwitchContent}>Register</button>
              </div>
            </div>
          </div>
        </div>
    );
};

export default LoginRegister;
